import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.addSession = this.addSession.bind(this);
    this.state = {
      bLength: 5,
      sLength: 25,
      session: "Session",
      minutes: 25,
      seconds: 0
    };
  }

 addSession() {
 
  this.setState({
    minutes: this.state.sLength
  })

   this.setState(prevState => {
     return { sLength: prevState.sLength + 1 }
   });

 }

  render() {
    return (
      <div>
        <div id="title">
          <h1>Pomodoro Clock</h1>
        </div>
        <div id="lengthDiv">
          <div id="breakLength">
            <h3>Break Length</h3>
            <div id="breakBtnsDiv">
              <input
                className='btn'
                type="button"
                id="increment"
                value="+"
              />
              <span id="span1">{this.state.bLength}</span>
              <input
                className='btn'
                type="button"
                id="decrement"
                value="-"              
              />
            </div>
          </div>
          {/* Session Length Area */}
          <div id="sessionLength">
            <h3>Session Length</h3>
            <div id="lengthBtnsDiv">
              <input
              className='btn'
                type="button"
                id="increment"
                value="+"       
                onClick={this.addSession}      
              />
              <span id="span2">{this.state.sLength}</span>
              <input
              className='btn'
                type="button"
                id="decrement"
                value="-"              
              />
            </div>
          </div>
        </div>
        <div id="clockDiv">
          <div id="clock">
            <h1 id="clockTitle">{this.state.session}</h1>
            <p id="clockTime">{this.state.minutes} : {this.state.seconds} </p>
            <div id="clockBtns">
              <button id="startBtn" className='btn'>Start</button>
              <button id="resetBtn" className='btn'>Reset</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
