import React from 'react';

class App extends React.Component {
    constructor(props) {
        super(props)
        this.handleBreakAdd = this.handleBreakAdd.bind(this);
        this.handleBreakMinus = this.handleBreakMinus.bind(this);
        this.handleSessionAdd = this.handleSessionAdd.bind(this);
        this.handleSessionMinus = this.handleSessionMinus.bind(this);
        this.state = {
            bLength: 5,
            sLength: 25
        }
    }

    handleBreakAdd() {
      this.setState(prevState => {
        return { bLength: prevState.bLength + 1 }
      })
    }

    handleBreakMinus() {
      if (this.state.bLength === '0') {
        this.setState({ bLength: 100 })
      }
      this.setState(prevState => {
        return { bLength: prevState.bLength - 1 }
      })
    }

    handleSessionAdd() {
      this.setState(prevState => {
        return{ sLength: prevState.sLength + 1 }
      })
    }

    handleSessionMinus() {
      this.setState(prevState => {
        return{ sLength: prevState.sLength - 1 }
      })
    }


    render() {
        return (
            <div>
                <div id='title'><h1>Pomodoro Clock</h1></div>
                  <div id='lengthDiv'>
                    <div id='breakLength'>
                        <h3>Break Length</h3>
                      <div>
                        <input type='button' id='increment' value='+' onClick={this.handleBreakAdd} />
                          {this.state.bLength}
                        <input type='button' id='decrement' value='-' onClick={this.handleBreakMinus} />
                      </div>
                    </div>
                      <div id='sessionLength'>
                          <h3>Session Length</h3>
                          <input type='button' id='increment' value='+' onClick={this.handleSessionAdd}/>
                          <span id='span2'>{this.state.sLength}</span>
                          <input type='button' id='decrement' value='-' onClick={this.handleSessionMinus}/>
                      </div>
                  </div>
                        <div id='clock'></div>
                          <div id='clockBtns'></div>
            </div>
        )
    }
}

export default App;
